import React, { useContext } from "react";
import "./Profession.css";
import "./Dashboard.css";
import AuthContext from "../context/AuthContext"; // Import authentication context

const ProfessionalDashboard = ({ bookingRequests, updateBookingStatus }) => {
  const { user } = useContext(AuthContext);

  // Filter bookings for the logged-in professional
  const professionalBookings = bookingRequests.filter(
    (request) => request.professional === user.name
  );

  return (
    <div className="dashboard">
      <h2>Welcome, {user.name}!</h2>
      {professionalBookings.length > 0 ? (
        <div className="grid-container">
          {professionalBookings.map((request) => (
            <div className="grid-item" key={request.id}>
              <h3>Booking Request</h3>
              <p><strong>User:</strong> {request.user}</p>
              <p><strong>Contact:</strong> {request.contact}</p>
              <p><strong>Address:</strong> {request.address}</p>
              <p><strong>Job Details:</strong> {request.jobDetails}</p>
              <p><strong>Status:</strong> {request.status || "Pending"}</p>
              <p><strong>Date:</strong> {request.date || "Not provided"}</p>
              <p><strong>Time:</strong> {request.time || "Not provided"}</p>
              <div className="action-buttons">
                <button
                  className="btn request"
                  onClick={() =>
                    updateBookingStatus(
                      bookingRequests.findIndex((req) => req.id === request.id),
                      "Accepted"
                    )
                  }
                >
                  Accept
                </button>
                <button
                  className="btn decline"
                  onClick={() =>
                    updateBookingStatus(
                      bookingRequests.findIndex((req) => req.id === request.id),
                      "Declined"
                    )
                  }
                >
                  Decline
                </button>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p>No booking requests available.</p>
      )}
    </div>
  );
};

export default ProfessionalDashboard;
